
class DataCleaningError(Exception):
    """Exception raised for errors during data cleaning."""
    pass

class DataTransformationError(Exception):
    """Exception raised for errors during data transformation."""
    pass

class DataVisualizationError(Exception):
    """Exception raised for errors during data visualization."""
    pass

class DataValidationError(Exception):
    """Exception raised for errors during data validation."""
    pass

class DataLoadingError(Exception):
    """Exception raised for errors during data loading."""
    pass
